type coord struct {
	x int
	y int
}

func main() {
	grid := [][]rune{}

	for i, line := range strings.Split(input, "\n") {
		grid = append(grid, []rune{})
		for _, r := range line {
			grid[i] = append(grid[i], r)
		}
	}

	pos := coord{}
	for x, r := range grid[0] {
		if r != ' ' {
			pos = coord{x, 0}
		}
	}

	direction := coord{0, 1}
	points := []rune{}

	for {
		if pos.y + direction.y >= len(grid) || pos.x + direction.x >= len(grid[pos.y + direction.y]) {
			break
		}

		next := grid[pos.y + direction.y][pos.x + direction.x]
		if next == ' ' {
			break
		}

		if next > 64 && next < 91 {
			points = append(points, next)
			pos = coord{pos.x + direction.x, pos.y + direction.y}
			continue
		}

		if next != '+' {
			pos = coord{pos.x + direction.x, pos.y + direction.y}
			continue
		}

		oldX := direction.x
		oldY := direction.y

		if direction.x == 0 {
			if pos.x + 1 < len(grid[pos.y + direction.y]) && grid[pos.y + direction.y][pos.x + 1] != ' ' {
				direction = coord{1, 0}
			} else {
				direction = coord{-1, 0}
			}
		} else {
			if pos.y + 1 < len(grid) && pos.x + direction.x < len(grid[pos.y + 1]) && grid[pos.y + 1][pos.x + direction.x] != ' ' {
				direction = coord{0, 1}
			} else {
				direction = coord{0, -1}
			}
		}
		pos = coord{pos.x + oldX, pos.y + oldY}
	}

	fmt.Println(string(points))
	fmt.Println(pos)
	fmt.Println(grid[1][5])
}
